#!/bin/sh
/usr/local/openjdk-8/bin/java -Djava.ext.dirs=/usr/local/openjdk-8/jre/lib/ext:/usr/local/openjdk-8/lib/ext  -server -Xms2g -Xmx2g -Xmn1g -XX:MetaspaceSize=128m -XX:MaxMetaspaceSize=320m -XX:-OmitStackTraceInFastThrow -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=/nacos/logs/java_heapdump.hprof -XX:-UseLargePages -Dnacos.member.list= -Xloggc:/nacos/logs/nacos_gc.log -verbose:gc -XX:+PrintGCDetails -XX:+PrintGCDateStamps -XX:+PrintGCTimeStamps -XX:+UseGCLogFileRotation -XX:NumberOfGCLogFiles=10 -XX:GCLogFileSize=100M -Dloader.path=/nacos/plugins,/nacos/plugins/health,/nacos/plugins/cmdb,/nacos/plugins/selector -Dnacos.home=/nacos -jar /nacos/target/nacos-server.jar  --spring.config.additional-location=file:/nacos/conf/ --logging.config=/nacos/conf/nacos-logback.xml --server.max-http-header-size=524288 "$@"